mapper.insert:
insert
  into member
  (
    user_no
  , user_id
  , user_pwd
  , user_name
  , email
  , gender
  , phone
  , address
  )
  values
  (
    seq_uno.nextval
  , #{userId}
  , #{userPwd}
  , #{userName}
  , #{email}
  , #{gender}
  , #{phone}
  , #{address}
  )

mapper.update:
update
			 member
	 set user_name = #{userName}
	   , email = #{email}
	   , phone = #{phone}
	   , address = #{address}
	   , gender = #{gender}
	   , modify_date = sysdate
 where user_id = #{userId}

update
			 member
	 set profile_url = #{profileURL}
	   , modify_date = sysdate
 where user_no = #{userNo}
